import { TOGGLE_CART_HIDDEN } from './cart-types';

const initialState = {
  hidden: true,
};

const cartReducer = (state = initialState, action) => {
  const { type } = action;
  switch (type) {
    case TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hidden: !state.hidden,
      };
    default:
      return state;
  }
};

export default cartReducer;
