import { connect } from 'react-redux';
import { clearItemFromCart, removeItem } from '../../redux/cart/cart-actions';
import './checkout-item.scss';

const CheckoutItem = ({ cartItem, clearAllItems, removeOne }) => {
  const { id, name, imageUrl, price, quantity } = cartItem;

  const deleteAllHandler = () => {
    clearAllItems(id);
  };

  const removeOneHandler = () => {
    removeOne(id);
  };
  return (
    <div className="checkout-item">
      <div className="image-container">
        <img src={imageUrl} alt="item" />
      </div>
      <span className="name">{name}</span>
      <span className="quantity">
        <div className="arrow">&#10094;</div>
        <span className="value"> {quantity}</span>
        <div className="arrow">&#10095;</div>
      </span>
      <span className="price">{price}</span>
      <div className="remove-button" onClick={deleteAllHandler}>
        &#10005;
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    clearAllItems: (id) => dispatch(clearItemFromCart(id)),
    removeOne: (id) => dispatch(removeItem(id)),
  };
};

export default connect(null, mapDispatchToProps)(CheckoutItem);
