import {
  ADD_ITEM,
  CLEAR_ITEM_FROM_CART,
  TOGGLE_CART_HIDDEN,
} from './cart-types';

export const toggleCartHidden = () => {
  return {
    type: TOGGLE_CART_HIDDEN,
  };
};

export const addToCart = (item) => {
  return {
    type: ADD_ITEM,
    payload: item,
  };
};

export const clearItemFromCart = (item) => {
  return {
    type: CLEAR_ITEM_FROM_CART,
    payload: item,
  };
};
