import { EMAIL_SIGN_IN_SUCCESS, GOOGLE_SIGN_IN_SUCCESS } from './user-types';

const initialState = {
  currentUser: null,
};

const userReducer = (state = initialState, action) => {
  const { type } = action;
  switch (type) {
    case GOOGLE_SIGN_IN_SUCCESS:
    case EMAIL_SIGN_IN_SUCCESS:
      return {
        ...state,
        currentUser: action.payload,
      };
    default:
      return state;
  }
};

export default userReducer;
